"use strict";

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

var requireIndex = require("requireindex");

//------------------------------------------------------------------------------
// Plugin Definition
//------------------------------------------------------------------------------


// import all rules in lib/rules
module.exports.rules = requireIndex(__dirname + "/rules");

// add link to documentation for each rule
var baseUrl = "https://github.wdf.sap.corp/S4FIORI-CD/eslint-plugin-fiori-custom/blob/master/docs/rules/";
var createNestedObject = function (base, names, value) {
    for (var i = 0; i < names.length - 1; i++) {
        base = base[names[i]] = base[names[i]] || {};
    }
    base[names[names.length - 1]] = value;
};
for (var property in module.exports.rules) {
    createNestedObject(module.exports.rules[property], ["meta", "docs", "url"], baseUrl + property + ".md");
}
module.exports.configs = {
    default: {
        extends:"../configure.eslintrc"
    }
};